import React, { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';
import Header from '../components/common/Header';
import { getMovies } from '../reducks/movies/selectors';
import API from '../API';
import { getFavourites } from '../reducks/favourites/selectors';
import { addFavourite, fetchFromLocalStorage } from '../reducks/favourites/operations';
import { useDispatch } from 'react-redux';
import IconFav from '../assets/img/icon-fav.png';
import Preview from '../components/common/Preview';
const api = new API();

const Categories = () => {
    const [categoryNewlyReleased, setCategoryNewlyReleased] = useState(null);
    const [categoryAnimated, setCategoryAnimated] = useState(null);
    const [categoryRomance, setCategoryRomance] = useState(null);
    const [categoryCrime, setCategoryCrime] = useState(null);
    const [showPreview, setShowPreview] = useState(false);
    const [selectedMovieId, setSelectedMovieId] = useState(null);

    const selector = useSelector(state => state);
    const dispatch = useDispatch();
    const movies = getMovies(selector);
    const favourites = getFavourites(selector);
    const clickfav = movie => {
        dispatch(addFavourite(movie));
    };
    const clickMovie = movieId => {
        setSelectedMovieId(movieId);
        setShowPreview(true);
    };

    useEffect(() => {
        dispatch(fetchFromLocalStorage);
        api.getMovies({ category_id: '1' })
            .then(movies => {
                setCategoryNewlyReleased(movies);
            })
            .catch(error => {
                alert('Failed to connect API: /movies/');
            });
        api.getMovies({ category_id: '2' })
            .then(movies => {
                setCategoryAnimated(movies);
            })
            .catch(error => {
                alert('Failed to connect API: /movies/');
            });
        api.getMovies({ category_id: '3' })
            .then(movies => {
                setCategoryRomance(movies);
            })
            .catch(error => {
                alert('Failed to connect API: /movies/');
            });
        api.getMovies({ category_id: '4' })
            .then(movies => {
                setCategoryCrime(movies);
            })
            .catch(error => {
                alert('Failed to connect API: /movies/');
            });
    }, []);
    return (
        <>
            {showPreview && <Preview setShowPreview={setShowPreview} selectedMovieId={selectedMovieId} />}
            <section>
                <Header />

                <div class="list">
                    <div class="left">
                        <div class="newly-rel">
                            <hr />
                            <h2>Newly Released</h2>
                            <hr />
                        </div>
                    </div>
                    {categoryNewlyReleased && categoryNewlyReleased.results.length > 0 ? (
                        <ul>
                            {categoryNewlyReleased.results.map(movie => (
                                <li>
                                    <div class="card">
                                        {movie &&
                                            Object.values(favourites).filter(
                                                favoriteMovie => movie.id == favoriteMovie.id
                                            ).length === 0 && (
                                                <img
                                                    class="fav"
                                                    onClick={() => {
                                                        clickfav(movie);
                                                    }}
                                                    src={IconFav}
                                                    alt=""
                                                />
                                            )}
                                        <img
                                            onClick={() => clickMovie(movie.id)}
                                            class="image"
                                            src={'https://res.cloudinary.com/dmhdtstvg/' + movie.image_mobile}
                                            alt=""
                                        />
                                    </div>
                                    <h1>{movie.name}</h1>
                                    <p>TV-MA | Action, Crime</p>
                                    <a href={movie.trailer_link} target="_blank"><button>Watch Trailer</button></a>
                                </li>
                            ))}
                        </ul>
                    ) : (
                        <div class="no-post">
